package Pack1;

public class Lab3 {

	public static void main(String[] args) {
		
		//TODO Auto-generated method stub
		
		int a, b, c;
		a=100;
		b=200;
		c=300;
		
		System.out.println("Addition: " + (a + b));
		System.out.println("Subtraction: " + (c - a));
		System.out.println("Multiplication: " + (c * a));
		System.out.println("Division: " + (c / a));
		System.out.println("Modulus: " + (c % a));

	}

}
